import { useState, useEffect } from 'react';
import './CreatePost.css'
import PersonImg from '../Images/person.svg'
import CloseImg from '../Images/close.svg'

function CreatePost({ SetCreatePost }) {
    return (
        <div className='CreatePostParent'>
            <div className='CreatePostTop'>
                <button className='CloseBtn' onClick={() => SetCreatePost(false)}><img src={CloseImg}></img></button>
                <button>Post</button>
            </div>
            {/* User Img */}
            <img className='UserImg' src={PersonImg}></img>
            <input></input>

        </div>
    )
}

export default CreatePost;
