import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react';

function App() {
  const name = "Divij";
  const isNameShowing = true;

  //Component
  const Person = (props) =>{
    return (
    <>
      <h3>Name : {props.name}</h3>
      <h3>Age : {props.age}</h3>
      <h4>Job : {props.job}</h4>
    </>
    )
  }

  // State
  const[Counter,SetCounter] = useState(0);

  // UseEffect
  // useEffect (()=> {
  //   alert("Count changed to " +Counter);
  // },[Counter]);
  return (
    <div className="App">
      {/* show name if name */}
      <h1>Hello {isNameShowing?name:""}</h1>

      {/* using conditions for HTML tags */}
      {
        name != null?
        <>
          <h1>your name - {name}</h1> 
        </>:// show name if name !null
        <>
          <h1>you dont have a name</h1>
        </>// else
      }

      {/* Adding person component */}
      <Person name = "Divij" age= "25" job="Developer"/>
      <Person name = "Chitra" age= {20+4} job="HR"/>
      <Person name = "John" age= "32" job="Manager"/>

      {/*changing counter using - Use state */}
      <button onClick={()=> SetCounter(prev=> prev-1)}>-</button>
      <h1>{Counter}</h1>
      <button onClick={()=> SetCounter(prev=> prev+1)}>+</button>

    </div>
  );
}

export default App;
